mapper.select:
SELECT * FROM (SELECT rownum com_list_no, b.* FROM (SELECT * FROM COMPANY WHERE COM_STATUS = 1 

AND com_name Like '%' || #{search} || '%'

ORDER BY COM_NO ) b ORDER BY com_list_no DESC) WHERE com_list_no BETWEEN #{startNo} AND #{endNo}

SELECT * FROM (SELECT rownum pla_list_no, b.* FROM (SELECT * FROM PLACE 

WHERE pla_name Like '%' || #{search} || '%'

ORDER BY PLA_NO ) b ORDER BY pla_list_no) WHERE pla_list_no BETWEEN #{startNo} AND #{endNo}
